import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [stage, setStage] = useState('question');
  const [question, setQuestion] = useState('');
  const [userThought, setUserThought] = useState('');
  const [aiResponse, setAiResponse] = useState('');
  const [isAiGenerating, setIsAiGenerating] = useState(false);
  const [canSubmit, setCanSubmit] = useState(false);
  const [error, setError] = useState('');

  // 2문장 이상 체크
  useEffect(() => {
    const sentences = userThought.split(/[.!?]+/).filter(s => s.trim().length > 0);
    setCanSubmit(sentences.length >= 2);
  }, [userThought]);

  const callAnthropicAPI = async (prompt) => {
    const apiKey = process.env.REACT_APP_ANTHROPIC_API_KEY;

    if (!apiKey) {
      throw new Error('API 키가 설정되지 않았습니다. .env 파일을 확인해주세요.');
    }

    const response = await fetch('https://api.anthropic.com/v1/messages', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'x-api-key': apiKey,
        'anthropic-version': '2023-06-01'
      },
      body: JSON.stringify({
        model: 'claude-sonnet-4-20250514',
        max_tokens: 1000,
        messages: [
          {
            role: 'user',
            content: prompt
          }
        ]
      })
    });

    if (!response.ok) {
      throw new Error(`API 오류: ${response.status}`);
    }

    const data = await response.json();
    return data.content[0].text;
  };

  const handleQuestionSubmit = async (e) => {
    e.preventDefault();
    if (!question.trim()) return;

    setStage('thinking');
    setIsAiGenerating(true);
    setError('');

    try {
      // 실제 API 호출
      const response = await callAnthropicAPI(question);
      setAiResponse(response);
    } catch (err) {
      console.error('API 호출 실패:', err);
      setError(err.message);
      // 에러가 발생해도 데모를 위해 기본 응답 제공
      setAiResponse(`[데모 모드] "${question}"에 대한 답변입니다. 실제 배포시 API 키를 설정하면 실제 Claude 응답을 받을 수 있습니다.`);
    } finally {
      setIsAiGenerating(false);
    }
  };

  const handleThoughtSubmit = (e) => {
    e.preventDefault();
    if (!canSubmit) return;

    // localStorage에 저장 (간단한 분석용)
    const timestamp = new Date().toISOString();
    const data = {
      timestamp,
      question,
      userThought,
      aiResponse,
      userThoughtLength: userThought.length,
      sentenceCount: userThought.split(/[.!?]+/).filter(s => s.trim().length > 0).length
    };

    const history = JSON.parse(localStorage.getItem('thinkBeforeAI') || '[]');
    history.push(data);
    localStorage.setItem('thinkBeforeAI', JSON.stringify(history));

    setStage('result');
  };

  const handleReset = () => {
    setStage('question');
    setQuestion('');
    setUserThought('');
    setAiResponse('');
    setIsAiGenerating(false);
    setError('');
  };

  return (
    <div className="App">
      {stage === 'question' && (
        <div className="stage-container">
          <h1>Think Before AI</h1>
          <p className="subtitle">AI와 함께 생각하기</p>

          <form onSubmit={handleQuestionSubmit} className="question-form">
            <textarea
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              placeholder="질문을 입력하세요..."
              className="question-input"
              rows="4"
            />
            <button type="submit" className="submit-btn" disabled={!question.trim()}>
              시작하기
            </button>
          </form>
        </div>
      )}

      {stage === 'thinking' && (
        <div className="stage-container">
          <div className="loading-container">
            {isAiGenerating && (
              <div className="loading-bar">
                <div className="loading-progress"></div>
              </div>
            )}
            <p className="loading-text">
              {isAiGenerating ? 'AI가 답변을 생성하는 중...' : 'AI 답변이 준비되었습니다'}
            </p>
            {error && <p className="error-text">⚠️ {error}</p>}
          </div>

          <div className="thinking-section">
            <h2>지금 본인의 생각은 어떤가요?</h2>
            <p className="prompt-text">아주 러프하게라도 적어봐요!</p>

            <form onSubmit={handleThoughtSubmit}>
              <textarea
                value={userThought}
                onChange={(e) => setUserThought(e.target.value)}
                placeholder="당신의 생각을 자유롭게 적어보세요..."
                className="thought-input"
                rows="8"
              />

              <div className="submit-info">
                <span className={canSubmit ? 'valid' : 'invalid'}>
                  {canSubmit ? '✓ 제출 가능합니다' : '최소 2문장 이상 작성해주세요'}
                </span>
              </div>

              <button 
                type="submit" 
                className="submit-btn" 
                disabled={!canSubmit || isAiGenerating}
              >
                제출하고 AI 답변 보기
              </button>
            </form>
          </div>
        </div>
      )}

      {stage === 'result' && (
        <div className="stage-container">
          <h1>비교해보기</h1>

          <div className="result-grid">
            <div className="result-card">
              <h3>당신의 생각</h3>
              <div className="content">
                {userThought}
              </div>
            </div>

            <div className="result-card">
              <h3>AI의 답변</h3>
              <div className="content">
                {aiResponse}
              </div>
            </div>
          </div>

          <button onClick={handleReset} className="reset-btn">
            새로운 질문하기
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
